mapper.insert:
INSERT INTO neacount (
   	acount_id, acount_number,member_id,
    money,acount_status
) VALUES (
    neacount_seq.nextval, #{acount_number}, #{member_id},
    0, #{acount_status}
)

mapper.delete:
DELETE FROM neacount
WHERE member_id = #{member_id}
AND acount_number = #{acount_number}

mapper.update:
	    UPDATE neacount
	    SET acount_status = #{acount_status}
	    WHERE member_id = #{member_id}
        AND acount_number = #{acount_number}

UPDATE neacount
SET money = money - #{autoAmount}
WHERE acount_id = #{autoAccount}
AND money >= #{autoAmount}
