mapper.select:
SELECT * FROM (
    SELECT a.*, ROWNUM rnum
    FROM (
        SELECT * FROM nemember
        ORDER BY signup_date DESC
    ) a
    WHERE ROWNUM <= #{offset} + #{size}
)
WHERE rnum > #{offset}

SELECT * FROM (
    SELECT a.*, ROWNUM rnum
    FROM (
        SELECT * FROM nemember



                    memberid LIKE '%' || #{searchKeyword} || '%'


                    nickname LIKE '%' || #{searchKeyword} || '%'


                    1=1



        ORDER BY signup_date DESC
    ) a
    WHERE ROWNUM <= #{offset} + #{size}
)
WHERE rnum > #{offset}

mapper.update:
UPDATE nemember
grade = #{grade}, name = #{name}, profile = #{profile}, nickname = #{nickname}, email = #{email}, phone = #{phone},
WHERE memberid = #{memberID}
